mapper.insert:
insert into m_sys (id, ip, k, val, 
  value_type, gmt_created, val_text
  )
values (#{id,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, #{k,jdbcType=VARCHAR}, #{val,jdbcType=REAL}, 
  #{valueType,jdbcType=INTEGER}, #{gmtCreated,jdbcType=TIMESTAMP}, #{valText,jdbcType=LONGVARCHAR}
  )

mapper.select:
    select 

    from m_sys
    where ip=#{ip,jdbcType=VARCHAR} and k=#{key} 

				AND gmt_created > #{startTime} and gmt_created < #{endTime} 

	 order by gmt_created desc

    select 

     ,

    from m_sys
    where ip=#{ip,jdbcType=VARCHAR} and k=#{key} 

				AND gmt_created > #{startTime} and gmt_created < #{endTime} 

	 order by gmt_created desc limit #{start},#{rows}

    select  count(*) from m_sys
    where ip=#{ip,jdbcType=VARCHAR} and k=#{key} 

				AND gmt_created > #{startTime} and gmt_created < #{endTime}

mapper.delete:
delete from m_sys
where 

   gmt_created < #{time}


   ip=#{ip,jdbcType=VARCHAR}
