mapper.insert:

insert into UMS_MSG_DRAFT (ID, USER_ID, RECV_ID,
  CONTENT, ACK, SEND_TIME,
  VALID_TIME, GMT_CREATED, GMT_MODIFIED,IDENTITY
  )
values (#{id,jdbcType=CHAR}, #{userId,jdbcType=VARCHAR}, #{recvId,jdbcType=VARCHAR},
  #{content,jdbcType=VARCHAR}, #{ack,jdbcType=INTEGER}, #{sendTime,jdbcType=TIMESTAMP},
  #{validTime,jdbcType=VARCHAR}, #{gmtCreated,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP},
  #{identity,jdbcType=CHAR}
  )

mapper.select:
SELECT

FROM UMS_MSG_DRAFT


		AND USER_ID = #{searchObj.userId,jdbcType=CHAR}



	order by ${searchObj.orderBy} ${searchObj.orderbyType}

limit #{recordFirst} , 10
